import Foundation
import Darwin
import QuartzCore

func executionTimeInterval(block: () -> ()) -> CFTimeInterval {
    let start = CACurrentMediaTime()
    block();
    let end = CACurrentMediaTime()
    return end - start
}

func P8() {

    let thousandDigits =
    ""

    var largestProduct = 0

    for index in 0 ... thousandDigits.characters.count - 13 {
            var product = 1
            for num in 0 ..< 13 {
                if let theInt = Int(String(thousandDigits[thousandDigits.index(thousandDigits.startIndex, offsetBy: index + num)])) {
                    if theInt == 0 {
                        break
                    }
                    else {
                        product *= theInt
                    }
                }
            }
            if product > largestProduct {
                largestProduct = product
            }
    }

    print("The largest product is \(largestProduct)")

}

let benchmark = executionTimeInterval {
    P8()
}

print("Time(ms): \(benchmark * 1000)")
